package gocql

import (
	"encoding/base64"
	"errors"
	"fmt"
	"testing"

	"github.com/golang/mock/gomock"
	"github.com/stretchr/testify/assert"

	"go.temporal.io/server/common/auth"
	"go.temporal.io/server/common/config"
	"go.temporal.io/server/common/resolver"
)

func TestNewCassandraCluster(t *testing.T) {
	tests := map[string]struct {
		cfg config.Cassandra
		err error
	}{
		"emptyConfig": {
			cfg: config.Cassandra{},
			err: nil,
		},
		"caCert_badBase64": {
			cfg: config.Cassandra{
				TLS: &auth.TLS{Enabled: true, CaData: "this isn't base64"},
			},
			err: base64.CorruptInputError(4),
		},
		"caCert_badPEM": {
			cfg: config.Cassandra{
				TLS: &auth.TLS{Enabled: true, CaData: "dGhpcyBpc24ndCBhIFBFTSBjZXJ0"},
			},
			err: errors.New("failed to load decoded CA Cert as PEM"),
		},
		"caCert_good": {
			cfg: config.Cassandra{
				TLS: &auth.TLS{
					Enabled: true,
					CaData:  "",
				},
			},
			err: nil,
		},
		"clientCert_badbase64cert": {
			cfg: config.Cassandra{
				TLS: &auth.TLS{
					Enabled:  true,
					CertData: "this ain't base64",
					KeyData:  "",
				},
			},
			err: base64.CorruptInputError(4),
		},
		"clientCert_badbase64key": {
			cfg: config.Cassandra{
				TLS: &auth.TLS{
					Enabled:  true,
					CertData: "",
					KeyData:  "this ain't base64",
				},
			},
			err: base64.CorruptInputError(4),
		},
		"clientCert_missingprivatekey": {
			cfg: config.Cassandra{
				TLS: &auth.TLS{
					Enabled:  true,
					CertData: "",
					KeyData:  "",
				},
			},
			err: fmt.Errorf("unable to generate x509 key pair: %w", errors.New("tls: failed to find any PEM data in key input")),
		},
		"clientCert_duplicate_cert": {
			cfg: config.Cassandra{
				TLS: &auth.TLS{
					Enabled:  true,
					CertData: "",
					CertFile: "/a/b/c",
				},
			},
			err: errors.New("only one of certData or certFile properties should be specified"),
		},
		"clientCert_duplicate_key": {
			cfg: config.Cassandra{
				TLS: &auth.TLS{
					Enabled: true,
					KeyData: "",
					KeyFile: "/a/b/c",
				},
			},
			err: errors.New("only one of keyData or keyFile properties should be specified"),
		},
		"clientCert_duplicate_ca": {
			cfg: config.Cassandra{
				TLS: &auth.TLS{
					Enabled: true,
					CaData:  "",
					CaFile:  "/a/b/c",
				},
			},
			err: errors.New("only one of caData or caFile properties should be specified"),
		},
	}

	for name, tc := range tests {
		t.Run(name, func(t *testing.T) {
			ctrl := gomock.NewController(t)
			r := resolver.NewMockServiceResolver(ctrl)
			_, err := NewCassandraCluster(tc.cfg, r)
			if !errors.Is(err, tc.err) {
				assert.Equal(t, tc.err, err)
			}
			ctrl.Finish()
		})
	}
}
